import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './LandingPage';
import StockfishGamePage from './StockfishGamePage';
import AnalysisBoardPage from './AnalysisBoardPage';
import SignupPage from './SignupPage';
import BlunderPunisher from './BlunderPunisher';
import WeaknessFinder from './WeaknessFinder';
import FeedbackPage from './FeedbackPage';
import TreeExplorer from './TreeExplorer';

// BLOCK DEFAULT ERROR HANDLING BEFORE REACT LOADS
(function overrideGlobalErrorHandling() {
  // Override `window.onerror` to block default error messages
  window.onerror = function (message, source, lineno, colno, error) {
    handleGlobalError(error || message);
    return true; // Fully blocks browser error display
  };

  // Override `window.onunhandledrejection` to catch promise errors
  window.onunhandledrejection = function (event) {
    if (event.preventDefault) event.preventDefault(); // Stop default error popups
    if (event.stopImmediatePropagation) event.stopImmediatePropagation(); // Block any further processing
    handleGlobalError(event.reason || 'Unhandled Promise Rejection');
    return true;
  };

  // Override console.error and console.warn to prevent log flashing
  console.error = function () {};
  console.warn = function () {};
})();

/**
 * Function to handle all global errors in a controlled manner.
 * Prevents default logging, ensures banner is displayed instantly.
 */
function handleGlobalError(errorMessage) {
  // Ensure error message is stored for next page load
  localStorage.setItem('errorMessage', errorMessage?.message || errorMessage || 'Unexpected Error');

  // Prevent console from logging error
  console.clear();

  // Immediately inject error banner before reload
  showErrorBanner(localStorage.getItem('errorMessage'));

  // Delay reload slightly (ensures banner renders first)
  setTimeout(() => {
    window.location.reload();
  }, 50); // 50ms is enough to inject banner first
}


/**
 * Function to create and display an error banner immediately.
 */
function showErrorBanner(message) {
  // Remove any existing banner
  const existingBanner = document.querySelector('.error-banner');
  if (existingBanner) {
    existingBanner.remove();
  }

  // Create the error banner
  const banner = document.createElement('div');
  banner.className = 'error-banner';
  banner.style.cssText = `
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    background-color: red;
    color: white;
    text-align: center;
    padding: 12px;
    font-size: 16px;
    font-weight: bold;
    z-index: 99999;
    box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
  `;
  banner.innerHTML = `<p>${message}</p>`;
  document.body.appendChild(banner);

  // Ensure the banner disappears after 3 seconds
  setTimeout(() => {
    banner.remove();
  }, 3000);
}


/**
 * Main App Component
 * - Uses useEffect to check for errors stored before reload
 */

function App() {
  useEffect(() => {
    // Check for stored error message on page load
    const errorMessage = localStorage.getItem('errorMessage');
    if (errorMessage) {
      showErrorBanner(errorMessage);

      // Clear error from localStorage to avoid persistence
      localStorage.removeItem('errorMessage');
    }
  }, []);

  return (
    <Router>
      <div>
        <Routes>
          <Route exact path="/" element={<LandingPage />} />
          <Route path="/play-stockfish" element={<StockfishGamePage />} />
          <Route path="/blunder-punisher" element={<BlunderPunisher />} />
          <Route path="/weakness-finder" element={<WeaknessFinder />} />
          <Route path="/analysis-board" element={<AnalysisBoardPage />} />
          <Route path="/tree-explorer" element={<TreeExplorer />} />
          <Route path="/feedback" element={<FeedbackPage />} />
          <Route path="/signup" element={<SignupPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
