import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Chess } from 'chess.js';
import './styles.css';
import * as dfd from 'danfojs';

const BlunderPunisher = () => {
    const [importedGames, setImportedGames] = useState([]);
    const [gamesCount, setGamesCount] = useState(0);
    const [commonLines, setCommonLines] = useState([]);
    const [filteredData, setFilteredData] = useState([]);


    const handleFileSelect = (event) => {
        console.log(0);
        const file = event.target.files[0];
        if (!file) return;
        console.log(1);

        const reader = new FileReader();
        reader.onload = (e) => {
            const fullPgn = e.target.result;
            let rawGames = fullPgn.split(/\[Event/gi).filter(Boolean);
            let games = rawGames.map(game => '[Event' + game.trim());
            console.log(2);
            setImportedGames(games);
            console.log(3);
            setGamesCount(games.length);
            console.log(4);
            processGames(games);
        };
        reader.readAsText(file);
    };

    const processGames = (games) => {
        console.log("a");
        const eventList = [];
        const siteList = [];
        const whiteList = [];
        const blackList = [];
        const resultList = [];
        const dateList = [];
        const whiteEloList = [];
        const blackEloList = [];
        const fullValList = [];
        const initialColnames = [];

        const movesList = [];
        const chess = new Chess();
        console.log("b");


        games.forEach(game => {
            chess.loadPgn(game);
            let moves = chess.history();
            movesList.push(moves);
            /* Errors? */
            const valList = [];
            let triangle = String(moves[0]) +"*"
            valList.push(triangle);
            /* Errors? Date */
            eventList.push(chess.header()["Event"]);
            siteList.push(chess.header()["Site"]);
            whiteList.push(chess.header()["White"]);
            blackList.push(chess.header()["Black"]);
            resultList.push(chess.header()["Result"]);
            whiteEloList.push(chess.header()["WhiteElo"]);
            blackEloList.push(chess.header()["BlackElo"]);
            for (let i = 0; i < 25; i++) {
                triangle += String(moves[i]);
                valList.push(triangle);

                if (i===24){
                    fullValList.push(valList);
                }

            }}); 
            console.log("c");

            for (let j = 1; j < 26; j++) {
                initialColnames.push("Line" +String(j));
            };
            console.log("d");
            const df = new dfd.DataFrame(fullValList, { columns: initialColnames});
            console.log("e");

            df.addColumn('Event', eventList);
            df.addColumn('Site', siteList);
            df.addColumn('White', whiteList);
            df.addColumn('Black', blackList);
            df.addColumn('WhiteResult1', resultList.map(val => val === '1-0' ? 1 : 0));
            //df.addColumn('Date', dateList);
            df.addColumn('WhiteElo', whiteEloList);
            df.addColumn('BlackElo', blackEloList);
            console.log("f");

            // Calculate MoveProbability1
            const moveProb1 = df.groupby(['Line1']).col(['Line1']).count();
            console.log("g");
            df.addColumn('MoveProbability1', df['Line1'].map(line1 => moveProb1['count'].values[moveProb1['Line1'].values.indexOf(line1)] / df.shape[0]));
            console.log("h");
            // Calculate subsequent move probabilities
            for (let i = 1; i < 26; i++) {
                df.addColumn(`WhiteResult${i + 1}`, df['WhiteResult1']);
                const moveProb = df.groupby([`Line${i}`, `Line${i + 1}`]).col([`Line${i}`]).count();
                df.addColumn(`MoveProbability${i + 1}`, df[`Line${i}`].map((line, index) => {
                    const count = moveProb['count'].values[moveProb[`${`Line${i}`}`].values.indexOf(line)];
                    const total = moveProb1['count'].values[moveProb1[`${`Line${i}`}`].values.indexOf(line)];
                    return count / total;
                }));
            }
            console.log("i");

            // Filter DataFrame based on conditions
            const opponentColor = 'Black'; // Replace with actual condition
            const opponentName = 'OpponentName'; // Replace with actual condition
            const filteredDF = df.query(df[opponentColor].eq(opponentName));
            console.log("j");

            setFilteredData(filteredDF);
            console.log("k");





        console.log(eventList);
        console.log(siteList);
        console.log(whiteList);
        console.log(blackList);
        console.log(resultList);
        console.log(whiteEloList);
        console.log(blackEloList);

        const commonLines = findCommonLines(movesList);
        setCommonLines(commonLines);
    };

    const findCommonLines = (movesList) => {
        const lineCounts = {};

        movesList.forEach(moves => {
            moves.forEach((move, index) => {
                const line = moves.slice(0, index + 1).join(' ');
                if (!lineCounts[line]) {
                    lineCounts[line] = 0;
                }
                lineCounts[line]++;
            });
        });

        const sortedLines = Object.entries(lineCounts).sort((a, b) => b[1] - a[1]);
        return sortedLines.slice(0, 5);
    };

    return (
        <div  className="blunder-punisher">
            <header>
                <nav>
                    <ul>
                        <li><Link to="/">Home</Link></li>
                        <li><Link to="/blunder-punisher">Blunder Punisher</Link></li>
                        <li><Link to="/">Weakness Finder</Link></li>
                        <li><Link to="/analysis-board">Analysis Board</Link></li>
                        <li><Link to="#about-us">About Us</Link></li>
                    </ul>
                </nav>
            </header>
            <main>
                <h1>Blunder Punisher</h1>
                <input type="file" onChange={handleFileSelect} />
                <p>Number of games in the file: {gamesCount}</p>
                <div className="games-list">
                    <ul>
                        {importedGames.map((game, index) => (
                            <li key={index}>{game.substring(0, 50)}...</li>
                        ))}
                    </ul>
                </div>
                <div className="common-lines">
                    <h2>Most Common Lines</h2>
                    <div className="common-lines">
                    <h2>Filtered Data</h2>
                    <pre>{JSON.stringify(filteredData, null, 2)}</pre>
                </div>
                </div>
            </main>
        </div>
    );
};

export default BlunderPunisher;
