import PropTypes from "prop-types";
import { Chess } from "chess.js";
import Chessboard from "chessboardjsx";
import EditBoard from "./EditBoard";

const STOCKFISH = window.STOCKFISH;
const game = new Chess();

class AnalysisBoard extends Component {
  static propTypes = {
    children: PropTypes.func,
    fen: PropTypes.string,
    pgn: PropTypes.string, // Add this line
  };

    state = {
      orientation: "white",
      evaluation: "",
      bestLine: "",
      currentPly: 1,
      future: [],
    };
    componentDidMount() {
      if (this.props.pgn) {
        console.log(1);
          game.loadPgn(this.props.pgn);
          this.future = game.history();
      }
      this.setState({ fen: game.fen() });
      this.engineGame().prepareMove();
  }

  goToEnd = () => {
  const moves = game.history();
  const tmp = new Chess();
  const previous = moves.length+this.future.length-1;
  if (previous >= 0){
    if (moves.length>0){
  for(var i=0;i<moves.length;i+=1) {
    tmp.move(moves[i]);
  }}

  if (this.future.length -1 >= 0){
    for(var i=0;i<this.future.length;i+=1) {
      tmp.move(this.future[i]);
    }
  const previous_fen = tmp.fen();
  const previous_pgn = tmp.pgn();
  this.future=[];
  game.load(previous_fen);
  game.loadPgn(previous_pgn);
  this.setState({ fen:previous_fen});
  if (!game.isGameOver()) {
    this.engineGame().prepareMove();
  }
}else{
return;}
}else{return;}
};


  goToNextMove = () => {
    if (this.future.length > 0){
      game.move(this.future[0]);
      this.setState({ fen:game.fen()});
      this.future.shift();
      if (!game.isGameOver()) {
        this.engineGame().prepareMove();
      }
    }
  return;
  };

  goToPreviousMove = () => {
    const moves = game.history();
    const tmp = new Chess();
    const previous = moves.length-1;
    if (previous >= 0){
    for(var i=0;i<previous;i+=1) {
      tmp.move(moves[i]);
    }
    const previous_fen = tmp.fen();
    const previous_pgn = tmp.pgn();
    this.future.unshift(moves[previous]);
    tmp.move(moves[previous]);
    game.load(previous_fen);
    game.loadPgn(previous_pgn);
    this.setState({ fen:previous_fen});
    if (!game.isGameOver()) {
      this.engineGame().prepareMove();
    }
  }
  return;
  };


  goToStart = () => {
    const moves = game.history();
    const tmp = new Chess();
    const previous_fen = tmp.fen();
    const previous_pgn = tmp.pgn();
    const previous = moves.length-1;
    if (previous >= 0){
    for(var i=0;i<previous;i+=1) {
      tmp.move(moves[i]);
    }
    this.future.unshift(...moves);
    game.load(previous_fen);
    game.loadPgn(previous_pgn);
    this.setState({ fen:previous_fen});
    if (!game.isGameOver()) {
      this.engineGame().prepareMove();
    }
  }
  return;
  };


  componentDidUpdate(prevProps) {
    if (prevProps.pgn !== this.props.pgn && this.props.pgn) {
        game.reset(); // Reset the game state
        game.loadPgn(this.props.pgn);
        this.setState({ fen: game.fen(), currentPly: 1, future: [] });
        this.engineGame().prepareMove();
    }
}

/*  componentDidUpdate(prevProps) {
    // Check if game position has changed
    if (prevProps.fen !== this.props.fen) {
      this.setState({ fen: this.props.fen});
      game.load(this.props.fen);
      this.engineGame().prepareMove();
    }
  }*/

  engineGame = (options) => {
    options = options || {};

    let engine =
      typeof STOCKFISH === "function"
        ? STOCKFISH()
        : new Worker(options.stockfishjs || "stockfish.js");

    engine.postMessage("uci");

    engine.onmessage = (event) => {
        const { currentPly } = this.state;
      let line;


      if (event && typeof event === "object") {
        line = event.data;
        // Extract evaluation and best continuation line from the Stockfish response
        const evaluation = this.extractEvaluation(line);
        const bestLine = this.extractBestLine(line);
        // Update state with evaluation and best continuation line
        this.setState({ evaluation, bestLine });
      } else {
        line = event;
      }

      // Process Stockfish analysis here
      const bestLine = this.extractBestLine(line);
      this.setState({ previousOutput: bestLine });

    };

    return {
      prepareMove: function () {
        // Send current position to Stockfish for analysis
        engine.postMessage("position fen " + game.fen());
        engine.postMessage("go depth 15");
      },
    };
  };
  handleEditBoard = () => {
    var T1 = document.getElementById("EditingOn");
    T1.style.display =  T1.style.display==="none"? "block": "none";

    var T2 = document.getElementById("EditingOff");
    T2.style.display =  T2.style.display==="block"? "none": "block";
};

loadGameFromPgn = () => {
  const { game } = this.state;
  const { pgn } = this.props;
  if (pgn) {
    game.load_pgn(pgn);
    this.setState({ fen: game.fen() });
  }
};
  onDrop = ({ sourceSquare, targetSquare }) => {
    console.log(1);
    const legalMoves = game.moves({ verbose: true });
    console.log(2);
    const move = legalMoves.find(
      (move) => move.from === sourceSquare && move.to === targetSquare
    );

    console.log(3);
    if (!move) {
      console.log("Illegal move");
      return;
    } 
    console.log(4);

    if (move.san==this.future[0]){
      this.future.shift();
  } else{this.future=[];}
  console.log(4);


  game.move({ from: sourceSquare, to: targetSquare, promotion: "q" });
  console.log(5);

    // Update state with new position
    // Update state with new position and add move to history
    this.setState((prevState) => ({
      fen: game.fen()
    }));
    console.log(6);
    this.setState(prevState => ({ currentPly: prevState.currentPly + 1 }));
    console.log(7);
    // Prepare Stockfish for analysis
    if (!game.isGameOver()) {
      this.engineGame().prepareMove();
    }
  };

  // Helper function to extract evaluation from the Stockfish response
  extractEvaluation = (line) => {
    // Extract the evaluation from the response

    const evalMatch = line.match(/score cp (-?\d+)/);
    if (evalMatch) {
        const evaluation = (Math.round(parseInt(evalMatch[1])*0.01 * 100) )/ 100;
        if (evaluation>=0){
          if (game.turn()=="b"){
            return (-evaluation); 
          }
          return "+"+(evaluation); 
        }
        if (game.turn()=="w"){
          return (evaluation); 
        }
        return "+"+(-evaluation); 


          // Reverse the sign for white's perspective
    }
    return null;
};

  // Helper function to extract the best continuation line from the Stockfish response
  // Helper function to extract the best continuation line from the Stockfish response and convert it into the desired format
// Helper function to extract the best continuation line from the Stockfish response in SAN format
// Helper function to extract the best continuation line from the Stockfish response in SAN format
extractBestLine = (line) => {
  const currentPly=game.history().length + 1;
  // Extract the best continuation line from the response
  const bestLineMatch = line.match(/pvSan (.*) bmc/);
  if (bestLineMatch) {
    const moves = bestLineMatch[1].split(" ");
    const sanMoves = [];
    for (let i = 0; i < 2 * Math.floor(moves.length / 2); i += 1) {
      const moveNumber = i+currentPly; // Adjust move numbering
      const sanMove = moveNumber %2 ===1? `${Math.floor((moveNumber+1)/2)}. ${moves[i]}` : `${Math.floor((moveNumber+1)/2)}... ${moves[i]}`; // Use "..." for Black's moves
      sanMoves.push(sanMove);
    }
    return sanMoves.join(" ");
  }
  return this.state.previousOutput;
};


render() {
    const { fen, orientation, evaluation, bestLine, currentPly, future } = this.state;
    return (
      <div>
        {this.props.children({
          fen: fen,
          orientation: orientation,
          evaluation: evaluation,
          bestLine: bestLine,
          currentPly: currentPly,
          future: future,
          goToEnd: this.goToEnd,
          goToNextMove: this.goToNextMove,
          goToPreviousMove: this.goToPreviousMove,
          goToStart: this.goToStart,
          onDrop: this.onDrop, // Pass onDrop function to children
          handleEditBoard:this.handleEditBoard,
        })}
        <button onClick={this.handleEditBoard}>Edit Board</button>
        <div id="EditingOff" style={{display:"block"}}>
        <Chessboard
          position={fen}
          width={540}
          onDrop={this.onDrop} // Pass onDrop function to Chessboard
          orientation={orientation}
          boardStyle={boardStyle}
        />
        </div>
        <div id="EditingOn" style={{display:"none"}}>
        <EditBoard in_fen={fen}> </EditBoard>
    </div>  
      </div>
    );
  }
}
export default AnalysisBoard;

