import React, { Component } from 'react';
import './ErrorBoundary.css';

class ErrorBoundary extends Component {
  state = { hasError: false, errorMessage: '' };

  static getDerivedStateFromError(error) {
    return { 
      hasError: true, 
      errorMessage: error.message || 'Unexpected error encountered' 
    };
  }

  componentDidCatch(error, errorInfo) {
    console.error('ErrorBoundary caught an error:', error, errorInfo);
    // Display banner and reload after 3 seconds
    displayErrorBanner(this.state.errorMessage);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-banner">
          <p>{this.state.errorMessage}</p>
        </div>
      );
    }
    return this.props.children;
  }
}

// Reusable banner display function
function displayErrorBanner(message) {
  const existingBanner = document.querySelector('.error-banner');
  if (!existingBanner) {
    const banner = document.createElement('div');
    banner.className = 'error-banner';
    banner.innerHTML = `<p>${message}</p>`;
    document.body.appendChild(banner);
    setTimeout(() => window.location.reload(), 3000);
  }
}

export default ErrorBoundary;
