import React, { Component } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import { Chess } from "chess.js";
import Chessboard from "chessboardjsx";
import EditBoard from "./EditBoard";

const STOCKFISH = window.STOCKFISH;
const game = new Chess();

class AnalysisBoard extends Component {
  static propTypes = {
    children: PropTypes.func,
    fen: PropTypes.string, // Add this line
    pgn: PropTypes.string, // Add this line
  };

  state = {
    orientation: "white",
    evaluation: "",
    bestLine: "",
    currentPly: 1,
    future: [],
    showEvaluation: false, // Add this line
    moveHistory: [],
    fullGame: [],
    importedGames: [], // Holds imported games
    showDropdown: false, // Toggles dropdown visibility
    selectedGame: null, // To store the selected game for display
    gamesDetails: [] // To store detailed metadata of games
  };
    componentDidMount() {
      if (this.props.pgn) {
          game.loadPgn(this.props.pgn);
          this.future = [];
          this.moveHistory= game.history();
          this.fullGame = this.moveHistory.concat(this.future);
          this.importedGames = [];

      }else{
        game.reset();
        this.future=[];
        this.moveHistory=[];
        this.fullGame = [];
        this.importedGames = [];
        this.setState({ fen: this.props.fen }); // Use the initial FEN passed as a prop
      }
      this.engineGame().prepareMove();
  }
  // Method to handle file selection and game import
  handleGameSelect = (pgn) => {
    game.reset();
    const tmp = new Chess();
    tmp.loadPgn(pgn);
    this.setState({ fen: this.props.fen || "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1" });
    this.future = tmp.history();
    this.moveHistory= tmp.history();
    this.fullGame = this.moveHistory;
    this.engineGame().prepareMove();
  }   
  handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) {
      return;
    }

    const reader = new FileReader();
    reader.onload = (e) => {
      const fullPgn = e.target.result;
      // Split the full PGN text using '[Event' as a delimiter, adding it back afterwards
      let rawGames = fullPgn.split(/\[Event/gi).filter(Boolean);  // remove any empty results
      let games = rawGames.map((game, index) => {
          // Prepend '[Event' to all but the first game
          return ('[Event' + game).trim();
      });
      const details = games.map(pgn => {
        const chess = new Chess();
        chess.loadPgn(pgn);
        const headers = chess.header();
        return {
          pgn: pgn,
          details: `${headers.White} vs ${headers.Black}, ${headers.Result}, ${headers.Date}`
        };
      });

