extractBestLine = (line) => {
  const currentPly=game.history().length + 1;
  // Extract the best continuation line from the response
  const bestLineMatch = line.match(/pvSan (.*) bmc/);
  if (bestLineMatch) {
    const moves = bestLineMatch[1].split(" ");
    const sanMoves = [];
    if (moves.length !==1){
    for (let i = 0; i < Math.min(2 * Math.floor(moves.length / 2),14); i += 1) {
      const moveNumber = i+currentPly; // Adjust move numbering
      const sanMove = moveNumber %2 ===1? `${Math.floor((moveNumber+1)/2)}.${moves[i]}` : `${Math.floor((moveNumber+1)/2)}...${moves[i]}`; // Use "..." for Black's moves
      sanMoves.push(sanMove);
    }
    return sanMoves.join(" ");
  } else{
    const sanMove = currentPly %2 ===1? `${Math.floor((currentPly+1)/2)}.${moves[0]}` : `${Math.floor((currentPly+1)/2)}...${moves[0]}`; // Use "..." for Black's moves
    return sanMove;
  }

  }
  return this.state.previousOutput;
};


