import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './LandingPage';
import StockfishGamePage from './StockfishGamePage';
import AnalysisBoardPage from './AnalysisBoardPage';
import SignupPage from './SignupPage';
import BlunderPunisher from './BlunderPunisher';
import WeaknessFinder from './WeaknessFinder';
import FeedbackPage from './FeedbackPage';
import TreeExplorer from './TreeExplorer';

// Global error handlers - MUST BE FIRST EXECUTED CODE
const installErrorHandlers = () => {
  const bannerStyles = `
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background-color: red;
    color: white;
    padding: 10px;
    text-align: center;
    z-index: 1000;
    box-shadow: 0 2px 5px rgba(0,0,0,0.2);
    font-size: 16px;
  `;

  window.onerror = (message, source, lineno, colno, error) => {
    const banner = document.createElement('div');
    banner.style.cssText = bannerStyles;
    banner.innerHTML = `<p style="margin:0">${
      error?.message?.includes('ArrayBuffer') 
        ? 'Array Buffer Allocation Failed' 
        : 'Unexpected error encountered'
    }</p>`;

    // Synchronously add to DOM
    document.body.prepend(banner);

    // Remove banner and reload after 3 seconds
    setTimeout(() => {
      banner.remove();
      window.location.reload();
    }, 3000);

    return true; // Prevent default error UI
  };

  window.onunhandledrejection = (e) => {
    const banner = document.createElement('div');
    banner.style.cssText = bannerStyles;
    banner.innerHTML = `<p style="margin:0">${
      e.reason?.message?.includes('ArrayBuffer') 
        ? 'Array Buffer Allocation Failed' 
        : 'Unexpected error encountered'
    }</p>`;

    // Synchronously add to DOM
    document.body.prepend(banner);

    // Remove banner and reload after 3 seconds
    setTimeout(() => {
      banner.remove();
      window.location.reload();
    }, 3000);

    return true; // Prevent default error UI
  };
};

// Install error handlers before React loads
installErrorHandlers();

function App() {
  return (
    <Router>
      <div>
        <Routes>
          <Route exact path="/" element={<LandingPage />} />
          <Route path="/play-stockfish" element={<StockfishGamePage />} />
          <Route path="/blunder-punisher" element={<BlunderPunisher />} />
          <Route path="/weakness-finder" element={<WeaknessFinder />} />
          <Route path="/analysis-board" element={<AnalysisBoardPage />} />
          <Route path="/tree-explorer" element={<TreeExplorer />} />
          <Route path="/feedback" element={<FeedbackPage />} />
          <Route path="/signup" element={<SignupPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
