import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './LandingPage';
import StockfishGamePage from './StockfishGamePage';
import AnalysisBoardPage from './AnalysisBoardPage';
import SignupPage from './SignupPage';
import BlunderPunisher from './BlunderPunisher';
import WeaknessFinder from './WeaknessFinder';
import FeedbackPage from './FeedbackPage';
import TreeExplorer from './TreeExplorer';

// Global error handlers - MUST BE AT THE TOP
window.onerror = (message, source, lineno, colno, error) => {
  const msg = error?.message?.includes('ArrayBuffer allocation failed') 
    ? 'Array Buffer Allocation Failed' 
    : 'Unexpected error encountered';

  displayErrorBanner(msg);
  return true; // Suppress default error UI
};

window.onunhandledrejection = (event) => {
  const msg = event.reason?.message?.includes('ArrayBuffer allocation failed')
    ? 'Array Buffer Allocation Failed'
    : 'Unexpected error encountered';

  displayErrorBanner(msg);
  return true; // Suppress default error UI
};

// Error banner styling (moved from CSS to inline)
const bannerStyles = `
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  background-color: red;
  color: white;
  padding: 10px;
  text-align: center;
  z-index: 1000;
  box-shadow: 0 2px 5px rgba(0,0,0,0.2);
  font-size: 16px;
`;

// Optimized error handling
let hasDisplayedBanner = false;

function displayErrorBanner(message) {
  if (hasDisplayedBanner) return;
  hasDisplayedBanner = true;

  // Create and show banner immediately
  const banner = document.createElement('div');
  banner.style.cssText = bannerStyles;
  banner.innerHTML = `<p style="margin:0;font-size:16px">${message}</p>`;
  document.body.prepend(banner); // Use prepend for higher priority

  // Reload after 3 seconds (not immediately)
  setTimeout(() => {
    window.location.reload();
  }, 3000);
}

function App() {
  useEffect(() => {
    // Clear any residual error state
    window.onload = () => {
      const existingBanner = document.querySelector('[style*="background-color: red"]');
      if (existingBanner) existingBanner.remove();
    };
  }, []);

  return (
    <Router>
      <div>
        <Routes>
          <Route exact path="/" element={<LandingPage />} />
          <Route path="/play-stockfish" element={<StockfishGamePage />} />
          <Route path="/blunder-punisher" element={<BlunderPunisher />} />
          <Route path="/weakness-finder" element={<WeaknessFinder />} />
          <Route path="/analysis-board" element={<AnalysisBoardPage />} />
          <Route path="/tree-explorer" element={<TreeExplorer />} />
          <Route path="/feedback" element={<FeedbackPage />} />
          <Route path="/signup" element={<SignupPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
