import React, { useState } from 'react';
import axios from 'axios';

const BlunderPunisher = () => {
    const [gamesCount, setGamesCount] = useState(0);
    const [commonLines, setCommonLines] = useState([]);

    const handleFileSelect = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await axios.post('http://localhost:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            setCommonLines(response.data);
            setGamesCount(Object.values(response.data).reduce((a, b) => a + b, 0));
        } catch (error) {
            console.error('Error uploading file:', error);
        }
    };

    return (
        <div className="blunder-punisher">
            <header>
                <nav>
                    <ul>
                        <li><a href="/">Home</a></li>
                        <li><a href="/blunder-punisher">Blunder Punisher</a></li>
                        <li><a href="/">Weakness Finder</a></li>
                        <li><a href="/analysis-board">Analysis Board</a></li>
                        <li><a href="#about-us">About Us</a></li>
                    </ul>
                </nav>
            </header>
            <main>
                <h1>Blunder Punisher</h1>
                <input type="file" onChange={handleFileSelect} />
                <p>Number of games in the file: {gamesCount}</p>
                <div className="common-lines">
                    <h2>Most Common Lines</h2>
                    <ul>
                        {Object.entries(commonLines).map(([line, count], index) => (
                            <li key={index}>{line} - {count}</li>
                        ))}
                    </ul>
                </div>
            </main>
        </div>
    );
};

export default BlunderPunisher;
