import React, { useState } from 'react';
import { Chess } from 'chess.js';
import * as pandas from 'pandas';

const BlunderPunisher = () => {
    const [importedGames, setImportedGames] = useState([]);
    const [gamesCount, setGamesCount] = useState(0);
    const [commonLines, setCommonLines] = useState([]);

    const handleFileSelect = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            const fullPgn = e.target.result;
            let rawGames = fullPgn.split(/\[Event/gi).filter(Boolean);
            let games = rawGames.map(game => '[Event' + game.trim());

            setImportedGames(games);
            setGamesCount(games.length);
        };
        reader.readAsText(file);
    };

    const processGames = () => {
        const gamesData = importedGames.map(pgn => {
            const game = Chess.PGN.readGame(pgn);
            const moves = game.moves().map(move => move.san).join('*');
            return {
                White: game.header('White'),
                Black: game.header('Black'),
                Event: game.header('Event'),
                Site: game.header('Site'),
                Date: game.header('Date') || game.header('UTCDate'),
                Result: game.header('Result'),
                Moves: moves
            };
        });

        let df = new pandas.DataFrame(gamesData);
        let expandedData = [];
        df.apply(row => {
            const moves = row.Moves.split('*');
            for (let i = 3; i < moves.length; i++) {
                expandedData.push({ ...row, Moves: moves.slice(0, i + 1).join('*') });
            }
        });

        let expandedDf = new pandas.DataFrame(expandedData);
        let commonLinesDf = expandedDf['Moves'].value_counts().reset_index();
        commonLinesDf.columns = ['Moves', 'Count'];
        let commonLines = commonLinesDf[commonLinesDf['Count'] > 3].head(5).to_dict();

        setCommonLines(commonLines);
    };

    return (
        <div className="blunder-punisher">
            <header>
                <nav>
                    <ul>
                        <li><a href="/">Home</a></li>
                        <li><a href="/blunder-punisher">Blunder Punisher</a></li>
                        <li><a href="/">Weakness Finder</a></li>
                        <li><a href="/analysis-board">Analysis Board</a></li>
                        <li><a href="#about-us">About Us</a></li>
                    </ul>
                </nav>
            </header>
            <main>
                <h1>Blunder Punisher</h1>
                <input type="file" onChange={handleFileSelect} />
                <p>Number of games in the file: {gamesCount}</p>
                <button onClick={processGames}>Process Games</button>
                <div className="common-lines">
                    <h2>Most Common Lines</h2>
                    <ul>
                        {commonLines.map((line, index) => (
                            <li key={index}>{line.Moves} - {line.Count}</li>
                        ))}
                    </ul>
                </div>
            </main>
        </div>
    );
};

export default BlunderPunisher;
