import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './styles.css';
import magnusImage from './nobackcarlsen.png';

import logo from './squarechesslogolong.png'; // Adjust the path as needed
import logoWhite from './squarechesslogolongwhite.png'; // Adjust the path as needed

const AboutMagnus = () => {
  const [selectedSection, setSelectedSection] = useState(null);
  const [isLogoHovered, setIsLogoHovered] = useState(false);

  const handleSectionClick = (section) => {
    setSelectedSection(selectedSection === section ? null : section);
  };

  const sections = {
    bio: "Magnus Carlsen, born November 30, 1990, is a Norwegian chess grandmaster who is the current World Chess Champion...",
    accomplishments: "World Chess Champion (2013–present), Four-time World Rapid Chess Champion...",
    skills: {
      positionalPlay: "Carlsen's positional play is often compared to that of chess legends like Anatoly Karpov...",
      endgameMastery: "Carlsen's endgame skills are considered some of the best in the history of chess...",
      versatility: "Magnus is known for his versatility and deep opening preparation..."
    }
  };

  return (
    <div className="about-magnus">
      <div className="background-image">
        <img src={magnusImage} alt="Magnus Carlsen" className="magnus-image" />
      </div>
      <header>
                <nav>
                    <ul>
                        <li onMouseEnter={() => setIsLogoHovered(true)}
                            onMouseLeave={() => setIsLogoHovered(false)}>
                            <Link to="/">
                                <img src={isLogoHovered ? logoWhite : logo} alt="Home" style={{ height: '40px' }} />
                            </Link>
                        </li>
                        <li><Link to="/blunder-punisher" className="prepare-link">Blunder Punisher</Link></li>
                        <li><Link to="/weakness-finder">Weakness Finder</Link></li>
                        <li><Link to="/analysis-board">Analysis Board</Link></li>
                        <li><Link to="/about-magnus">About Magnus</Link></li>
                    </ul>
                </nav>
          <h1>Magnus Carlsen</h1>
        <p>The Chess Maestro</p>
      </header>
      <main>
      <div className="floating-pieces">
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/f/f0/Chess_kdt45.svg" className={`chess-piece king-black piece-${i}`} alt="king-black" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/4/47/Chess_qdt45.svg" className={`chess-piece queen-black piece-${i}`} alt="queen-black" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/f/ff/Chess_rdt45.svg" className={`chess-piece rook-black piece-${i}`} alt="rook-black" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/9/98/Chess_bdt45.svg" className={`chess-piece bishop-black piece-${i}`} alt="bishop-black" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/e/ef/Chess_ndt45.svg"
            className={`chess-piece knight-black piece-${i}`} alt="knight-black" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/c/c7/Chess_pdt45.svg" className={`chess-piece pawn-black piece-${i}`} alt="pawn-black" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/4/42/Chess_klt45.svg" className={`chess-piece king-white piece-${i}`} alt="king-white" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/1/15/Chess_qlt45.svg" className={`chess-piece queen-white piece-${i}`} alt="queen-white" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/7/72/Chess_rlt45.svg" className={`chess-piece rook-white piece-${i}`} alt="rook-white" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/b/b1/Chess_blt45.svg" className={`chess-piece bishop-white piece-${i}`} alt="bishop-white" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/7/70/Chess_nlt45.svg" className={`chess-piece knight-white piece-${i}`} alt="knight-white" />
        ))}
        {[...Array(50)].map((_, i) => (
          <img key={i} src="https://upload.wikimedia.org/wikipedia/commons/4/45/Chess_plt45.svg" className={`chess-piece pawn-white piece-${i}`} alt="pawn-white" />
        ))}
        {/* Repeat for other pieces */}
        <div className="hidden-section bio" onClick={() => handleSectionClick('bio')}>
          <span>Biography</span>
        </div>
        <div className="hidden-section accomplishments" onClick={() => handleSectionClick('accomplishments')}>
          <span>Accomplishments</span>
        </div>
        <div className="hidden-section skills" onClick={() => handleSectionClick('skills')}>
          <span>Skills</span>
        </div>
      </div>

      {selectedSection && (
        <div className={`section-content ${selectedSection}`}>
          {selectedSection === 'bio' && <p>{sections.bio}</p>}
          {selectedSection === 'accomplishments' && (
            <ul>{sections.accomplishments.split(', ').map(acc => <li key={acc}>{acc}</li>)}</ul>
          )}
          {selectedSection === 'skills' && (
            <div>
              <h3>Positional Play</h3>
              <p>{sections.skills.positionalPlay}</p>
              <h3>Endgame Mastery</h3>
              <p>{sections.skills.endgameMastery}</p>
              <h3>Versatility</h3>
              <p>{sections.skills.versatility}</p>
            </div>
          )}
        </div>
      )}
      </main>
      <footer>
                <p>&copy; 2024 SquareChess.org All rights reserved.</p>
            </footer>
    </div>
  );
};

export default AboutMagnus;
