import React, { Component } from 'react';
import './ErrorBoundary.css'; // Create this CSS file for styling

class ErrorBoundary extends Component {
  state = { hasError: false };

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    console.error('ErrorBoundary caught an error', error, errorInfo);
  }

  handleClose = () => {
    this.setState({ hasError: false });
    window.location.reload(); // Reload to the default state
  };

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-banner">
          <span>Sorry, an unexpected error occurred.</span>
          <button className="close-button" onClick={this.handleClose}>Close</button>
        </div>
      );
    }

    return this.props.children; 
  }
}

export default ErrorBoundary;
