import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Chess } from 'chess.js';
import './styles.css';

// Import both logo images
import logo from './squarechesslogolong.png'; // Adjust the path as needed
import logoWhite from './squarechesslogolongwhite.png'; // Adjust the path as needed

const STOCKFISH = window.STOCKFISH;

const BlunderPunisher = () => {
    const [importedGames, setImportedGames] = useState(JSON.parse(localStorage.getItem('importedGames')) || []);
    const [gamesCount, setGamesCount] = useState(importedGames.length);
    const [commonLines, setCommonLines] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [finalLongDF, setFinalLongDF] = useState([]);
    const [thePgnList, setThePgnList] = useState([]);
    const [evaluation, setEvaluation] = useState("");
    const [bestLine, setBestLine] = useState("");
    const [previousOutput, setPreviousOutput] = useState("");
    const [isLogoHovered, setIsLogoHovered] = useState(false);
    const [opponentName, setOpponentName] = useState('');
    const [opponentColor, setOpponentColor] = useState('White');
    const [precisionLevel, setPrecisionLevel] = useState('Amateur/SemiPro');
    const [processing, setProcessing] = useState(false);
    const [activeDropdown, setActiveDropdown] = useState(null);
    const [inputMethod, setInputMethod] = useState('pgn'); // Default to pgn


    let stockfish = typeof STOCKFISH === "function" ? STOCKFISH() : new Worker("stockfish.js");
    stockfish.postMessage("uci");

    useEffect(() => {
        const savedState = localStorage.getItem('blunderPunisherState');
        if (savedState) {
            const {
                importedGames, gamesCount, commonLines, filteredData, finalLongDF,
                thePgnList, evaluation, bestLine, previousOutput, opponentName,
                opponentColor, precisionLevel, inputMethod
            } = JSON.parse(savedState);
            setImportedGames(importedGames);
            setGamesCount(gamesCount);
            setCommonLines(commonLines);
            setFilteredData(filteredData);
            setFinalLongDF(finalLongDF);
            setThePgnList(thePgnList);
            setEvaluation(evaluation);
            setBestLine(bestLine);
            setPreviousOutput(previousOutput);
            setOpponentName(opponentName);
            setOpponentColor(opponentColor);
            setPrecisionLevel(precisionLevel);
            setInputMethod(inputMethod);
        }
    }, []);

    useEffect(() => {
        const combinedPgn = localStorage.getItem('combinedPgn');
        if (combinedPgn) {
            processCombinedPgn(combinedPgn);
            localStorage.removeItem('combinedPgn');
        }
    }, []);

    useEffect(() => {
        const state = {
            importedGames, gamesCount, commonLines, filteredData, finalLongDF,
            thePgnList, evaluation, bestLine, previousOutput, opponentName,
            opponentColor, precisionLevel, inputMethod
        };
        localStorage.setItem('blunderPunisherState', JSON.stringify(state));
    }, [
        importedGames, gamesCount, commonLines, filteredData, finalLongDF,
        thePgnList, evaluation, bestLine, previousOutput, opponentName,
        opponentColor, precisionLevel, inputMethod
    ]);



    const handleFileSelect = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            const fullPgn = e.target.result;
            let rawGames = fullPgn.split(/\[Event/gi).filter(Boolean);
            let games = rawGames.map(game => '[Event' + game.trim());

            setImportedGames(games);
            setGamesCount(games.length);
        };
        reader.readAsText(file);
    };

    const fetchLichessGames = async (username) => {
        const response = await fetch(`https://lichess.org/api/games/user/${username}?max=1000&moves=true&pgnInJson=false`);
        const data = await response.text(); // Get the response as text (PGN format)
        return data;
    };

    const handleReset = () => {
        setImportedGames([]);
        setGamesCount(0);
        setCommonLines([]);
        setFilteredData([]);
        setFinalLongDF([]);
        setThePgnList([]);
        setEvaluation("");
        setBestLine("");
        setPreviousOutput("");
        setProcessing(false);
        setInputMethod('pgn');
        localStorage.removeItem('blunderPunisherState');
    };




    const handleStart = async () => {
        setProcessing(true);
        let games = [];

        if (inputMethod === 'lichess') {
            const data = await fetchLichessGames(opponentName);
            let rawGames = data.split(/\[Event/gi).filter(Boolean);
            games = rawGames.map(game => '[Event' + game.trim());
        } else {
            games = importedGames;
        }

        processGames(games);
    };

    const processGames = (games) => {
        const eventList = [];
        const siteList = [];
        const whiteList = [];
        const blackList = [];
        const resultList = [];
        const dateList = [];
        const whiteEloList = [];
        const blackEloList = [];
        const fullValList = [];
        const initialColnames = [];
        const movesList = [];
        const pgnList = [];
        const chess = new Chess();

        games.forEach((game, index) => {
            chess.loadPgn(game);
            chess.deleteComments();
            let moves = chess.history();
            movesList.push(moves);

            const valList = [];
            let triangle = String(moves[0]) + "*";
            valList.push(triangle);

            pgnList.push(chess.pgn());
            eventList.push(chess.header()["Event"]);
            siteList.push(chess.header()["Site"]);
            whiteList.push(chess.header()["White"]);
            blackList.push(chess.header()["Black"]);
            resultList.push(chess.header()["Result"]);
            dateList.push(chess.header()["Date"]);
            whiteEloList.push(chess.header()["WhiteElo"]);
            blackEloList.push(chess.header()["BlackElo"]);

            for (let i = 1; i < 25 && i < moves.length; i++) {
                triangle += String(moves[i]) + "*";
