import React, { Component } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import { Chess } from "chess.js";
//import { Chessboard } from 'react-chessboard';
import EditBoard from "./EditBoard";
import Chessboard from "chessboardjsx";

const STOCKFISH = window.STOCKFISH;
const game = new Chess();

class AnalysisBoard extends Component {
static propTypes = {
children: PropTypes.func,
fen: PropTypes.string,
pgn: PropTypes.string,
};

state = {
orientation: "white",
evaluation: "",
bestLine: "",
currentPly: 1,
future: [],
showEvaluation: false,
moveHistory: [],
fullGame: [],
importedGames: JSON.parse(localStorage.getItem('importedGames')) || [],
showDropdown: false,
selectedGame: null,
gamesDetails: [],
showEditBoard: false,
fen: this.props.fen || 'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1',
fileInputKey: Date.now(), // To reset the file input
};

componentDidMount() {
const combinedPgn = localStorage.getItem('combinedPgn');
if (combinedPgn) {
this.processCombinedPgn(combinedPgn);
localStorage.removeItem('combinedPgn');
}else if (this.props.pgn) {
game.loadPgn(this.props.pgn);
this.future = [];
this.moveHistory = game.history();
this.fullGame = this.moveHistory.concat(this.future);
this.importedGames = [];
} else {
game.reset();
this.future = [];
this.moveHistory = [];
this.fullGame = [];
this.setState({ fen: this.props.fen });
}
this.engineGame().prepareMove();
// Add keyboard event listener
document.addEventListener("keydown", this.handleKeyDown);
}
componentWillUnmount() {
// Remove keyboard event listener
document.removeEventListener("keydown", this.handleKeyDown);
}

processCombinedPgn(combinedPgn) {
const rawGames = combinedPgn.split(/\[Event/gi).filter(Boolean);
const games = rawGames.map(game => '[Event' + game.trim());

const details = games.map(pgn => {
const chess = new Chess();
chess.loadPgn(pgn);
const headers = chess.header();
return {
pgn: pgn,
details: `${headers.White} vs ${headers.Black}, ${headers.Result}, ${headers.Date}`
};
});

this.setState({
importedGames: details,
selectedGame: null,
showDropdown: true,
fen: this.props.fen || "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1"
});

localStorage.setItem('importedGames', JSON.stringify(details));
}
handleKeyDown = (event) => {
switch (event.key) {
case "ArrowRight":
this.goToNextMove();
break;
case "ArrowLeft":
this.goToPreviousMove();
break;
case "ArrowDown":
this.goToStart();
break;
case "ArrowUp":
this.goToEnd();
break;
default:
break;
}
};

handleGameSelect = (pgn) => {
game.reset();
const tmp = new Chess();
tmp.loadPgn(pgn);
this.setState({
fen: this.props.fen || "rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1",
showDropdown: true
});
this.future = tmp.history();
this.moveHistory = tmp.history();
this.fullGame = this.moveHistory;
this.engineGame().prepareMove();
};

handleFileSelect = async (event) => {
const file = event.target.files[0];
if (!file) {
return;
}

const reader = new FileReader();
reader.onload = (e) => {
  const fullPgn = e.target.result;
  let rawGames = fullPgn.split(/\[Event/gi).filter(Boolean);
  let games = rawGames.map((game, index) => {
    return ('[Event' + game).trim();
  });
  const details = games.map(pgn => {
    const chess = new Chess();
    chess.loadPgn(pgn);
    const headers = chess.header();
    return {
      pgn: pgn,
      details: `${headers.White} vs ${headers.Black}, ${headers.Result}, ${headers.Date}`
    };
  });

  this.setState({
    importedGames: [...this.state.importedGames, ...details],
    selectedGame: null,
    fileInputKey: Date.now() // Reset the file input
  });
  localStorage.setItem('importedGames', JSON.stringify([...this.state.importedGames, ...details]));
};
reader.readAsText(file);
};

handleRemoveFile = () => {
this.setState({
importedGames: [],
selectedGame: null,
fileInputKey: Date.now() // Reset the file input
});
localStorage.removeItem('importedGames');
};

displayGameDetails = (pgn) => {
const chess = new Chess();
chess.loadPgn(pgn);
const headers = chess.header();
return `${headers.White} vs ${headers.Black}, ${headers.Result}, ${headers.Date}`;
};

handleFlipBoard = () => {
this.setState(({ orientation }) => ({
orientation: orientation === 'white' ? 'black' : 'white',
}));
};

handleResetBoard = () => {
game.reset();
this.setState(({ fen: game.fen() }));
this.future = [];
this.moveHistory = [];
this.fullGame = [];
this.engineGame().prepareMove();
};

toggleStockfishEvaluation = () => {
this.setState(prevState => ({
showEvaluation: !prevState.showEvaluation
}));
};

goToEnd = () => {
const moves = game.history();
const tmp = new Chess();
const previous = moves.length + this.future.length - 1;
if (previous >= 0) {
if (moves.length > 0) {
for (var i = 0; i < moves.length; i += 1) {
tmp.move(moves[i]);
}
}

  if (this.future.length - 1 >= 0) {
    for (var i = 0; i < this.future.length; i += 1) {
      tmp.move(this.future[i]);
    }
    const previous_fen = tmp.fen();
    const previous_pgn = tmp.pgn();
    this.future = [];
    game.load(previous_fen);
    game.loadPgn(previous_pgn);
    this.setState({ fen: previous_fen });
    this.moveHistory = game.history();
    this.setState({ currentPly: this.moveHistory.length });
    if (!game.isGameOver()) {
      this.engineGame().prepareMove();
    }
  } else {
    return;
  }
} else {
  return;
}
};

goToNextMove = () => {
if (this.future.length > 0) {
game.move(this.future[0]);
this.setState({ fen: game.fen() });
this.future.shift();
this.moveHistory = game.history();
this.setState({ currentPly: this.moveHistory.length });
if (!game.isGameOver()) {
this.engineGame().prepareMove();
}
}
return;
};

goToPreviousMove = () => {

const moves = game.history();
console.log(game.fen());

const tmp = new Chess();
const previous = moves.length - 1;
if (previous >= 0) {
  for (var i = 0; i < previous; i += 1) {
    tmp.move(moves[i]);
  }
  const previous_fen = tmp.fen();
  console.log(previous_fen);
  console.log(previous_fen);

  const previous_pgn = tmp.pgn();
  this.future.unshift(moves[previous]);
  tmp.move(moves[previous]);
  game.load(previous_fen);
  game.loadPgn(previous_pgn);
  this.setState({ fen: previous_fen });
  this.moveHistory = game.history();
  this.setState({ currentPly: this.moveHistory.length });
  if (!game.isGameOver()) {
    this.engineGame().prepareMove();
  }
}
return;
};

goToStart = () => {
const moves = game.history();
const tmp = new Chess();
const previous_fen = tmp.fen();
const previous_pgn = tmp.pgn();
const previous = moves.length - 1;
if (previous >= 0) {
for (var i = 0; i < previous; i += 1) {
tmp.move(moves[i]);
}
this.future.unshift(...moves);
game.load(previous_fen);
game.loadPgn(previous_pgn);
this.setState({ fen: previous_fen });
this.moveHistory = [];
this.setState({ currentPly: this.moveHistory.length });
if (!game.isGameOver()) {
this.engineGame().prepareMove();
}
}
return;
};

toggleDropdown = () => {
this.setState((prevState) => ({
showDropdown: !prevState.showDropdown,
}));
};

goToMove = (moveIndex) => {
const moves = this.fullGame;
const tmp = new Chess();

for (let i = 0; i <= moveIndex; i++) {
  tmp.move(moves[i]);
}

const previous_fen = tmp.fen();
const previous_pgn = tmp.pgn();
game.loadPgn(previous_pgn);
this.setState({ fen: previous_fen });

const newMoveHistory = tmp.history();
this.moveHistory = newMoveHistory;
this.future = moves.slice(moveIndex + 1);
this.setState({ currentPly: this.moveHistory.length });

if (!game.isGameOver()) {
  this.engineGame().prepareMove();
}
};

componentDidUpdate(prevProps, prevState) {
if (prevState.selectedGame !== this.state.selectedGame && this.state.selectedGame) {
const chess = new Chess();
chess.loadPgn(this.state.selectedGame);
this.setState({ fen: chess.fen() });
}
if (prevProps.pgn !== this.props.pgn && this.props.pgn) {
game.reset();
game.loadPgn(this.props.pgn);
this.setState({ fen: game.fen(), currentPly: 1, future: [] });
this.moveHistory = game.history();
this.engineGame().prepareMove();
}
}

engineGame = (options) => {
options = options || {};

let engine =
  typeof STOCKFISH === "function"
    ? STOCKFISH()
    : new Worker(options.stockfishjs || "stockfish.js");

engine.postMessage("uci");

engine.onmessage = (event) => {
  const { currentPly } = this.state;
  let line;

  if (event && typeof event === "object") {
    line = event.data;
    const evaluation = this.extractEvaluation(line);
    const bestLine = this.extractBestLine(line);
    this.setState({ evaluation, bestLine });
  } else {
    line = event;
  }

  const bestLine = this.extractBestLine(line);
  this.setState({ previousOutput: bestLine });
};

return {
  prepareMove: function () {
    engine.postMessage("position fen " + game.fen());
    engine.postMessage("go depth 17");
  },
};
};

handleEditBoard = () => {
this.setState(prevState => ({
showEditBoard: !prevState.showEditBoard
}));
};

updateFenAndCloseEdit = (newFen) => {
game.reset();
game.load(newFen);
this.setState({ fen: newFen, showEditBoard: false });
this.setState({ currentPly: 0, future: [] });
this.moveHistory = [];
this.engineGame().prepareMove();
};

onDrop = ({ sourceSquare, targetSquare }) => {
const legalMoves = game.moves({ verbose: true });
const move = legalMoves.find(
(move) => move.from === sourceSquare && move.to === targetSquare
);

if (!move) {
  console.log("Illegal move");
  return;
}

if (move.san === this.future[0]) {
  this.future.shift();
} else {
  this.future = [];
}

game.move({ from: sourceSquare, to: targetSquare, promotion: "q" });
console.log(game.fen())
this.moveHistory = game.history();
this.setState({ currentPly: this.moveHistory.length });
this.fullGame = this.moveHistory.concat(this.future);

this.setState((prevState) => ({
  fen: game.fen()
}));
if (!game.isGameOver()) {
  this.engineGame().prepareMove();
}
};

extractEvaluation = (line) => {
const mateEvalMatch = line.match(/score mate (.+)/);
const evalMatch = line.match(/score cp (-?\d+)/);
if (evalMatch) {
const evaluation = (Math.round(parseInt(evalMatch[1]) * 0.01 * 100)) / 100;
if (evaluation >= 0) {
if (game.turn() === "b") {
return (-evaluation);
}
return "+" + (evaluation);
}
if (game.turn() === "w") {
return (evaluation);
}
return "+" + (-evaluation);
} else {
if (mateEvalMatch) {
const evaluation = (parseInt(mateEvalMatch[1]));
if (evaluation >= 0) {
if (game.turn() === "b") {
return ("Black Mate in " + (evaluation));
} return ("White Mate in " + (evaluation));
}
if (game.turn() === "w") {
return ("Black Mate in " + (-evaluation));
} return ("White Mate in " + (-evaluation));
}
} return null;
};

extractBestLine = (line) => {
const currentPly = game.history().length + 1;
const bestLineMatch = line.match(/pvSan (.*) bmc/);
if (bestLineMatch) {
const moves = bestLineMatch[1].split(" ");
const sanMoves = [];
if (moves.length !== 1) {
for (let i = 0; i < Math.min(2 * Math.floor(moves.length / 2), 14); i += 1) {
const moveNumber = i + currentPly;
const sanMove = moveNumber % 2 === 1 ? `${Math.floor((moveNumber + 1) / 2)}.${moves[i]}` : `${Math.floor((moveNumber + 1) / 2)}...${moves[i]}`;
sanMoves.push(sanMove);
}
return sanMoves.join(" ");
} else {
const sanMove = currentPly % 2 === 1 ? `${Math.floor((currentPly + 1) / 2)}.${moves[0]}` : `${Math.floor((currentPly + 1) / 2)}...${moves[0]}`;
return sanMove;
}
}
return this.state.previousOutput;
};

render() {
const { fen, orientation, evaluation, bestLine, currentPly, future, showEvaluation, moveHistory, fullGame, importedGames, showDropdown, showEditBoard, fileInputKey } = this.state;

console.log("Rendering Chessboard with custom pieces");
return (
  <div>
    <div className="analysis-toolkit" id="EditingOff" style={{ display: showEditBoard ? "none" : "block" }}>
    <div className="dropdown-header" onClick={this.toggleDropdown}>
        Games Imported From PGN Database: {importedGames.length}
      </div>
      {showDropdown && (
        <div className="games-list">
          {importedGames.length === 0 ? (
            <p>Click "Choose File" to Import games</p>
          ) : (
            <ul>
              {importedGames.map((game, index) => (
                <li className="game-item" key={index} onClick={() => this.handleGameSelect(game.pgn)}>
                  {game.details}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
     <div className="file-buttons">
<input
key={fileInputKey}
className="file-button"
type="file"
onChange={this.handleFileSelect}
style={{ position: 'absolute', bottom: 130, left: 10 }}
/>
<button
className="remove-file-button"
onClick={this.handleRemoveFile}
style={{ position: 'absolute', bottom: 150, left: 280 }}>

Remove File(s)
  </button>
</div>
      <div style={boardsContainer}>
        <div className="analysis-board-container" style={{ display: "block" }}>
        <Chessboard
position={fen}
width={520}
pieces={customPieces({ squareWidth:65, isDragging: false })}
onDrop={this.onDrop}
orientation={orientation}
boardStyle={boardStyle}
/>
</div>
</div>
      <div className="move-history">
        <h3>Move History</h3>
        <ul>
          {this.fullGame && this.fullGame.map((move, index) => (
            <li
              key={index}
              className={index === currentPly - 1 ? 'selected' : ''}
              onClick={() => this.goToMove(index)}
            >
              {index % 2 === 0 ? `${index / 2 + 1}. ` : `${(index - 1) / 2 + 1}...`}{move}
            </li>
          ))}
        </ul>
      </div>
      <div className="control-panel">
        <button onClick={this.handleEditBoard}>Edit</button>
        <button onClick={this.handleFlipBoard}>Flip</button>
        <button onClick={this.handleResetBoard}>Reset</button>
        <button onClick={this.toggleStockfishEvaluation}>üêü</button>
        <button onClick={this.goToStart}>Start</button>
        <button onClick={this.goToPreviousMove}>‚á¶</button>
        <button onClick={this.goToNextMove}>‚á®</button>
        <button onClick={this.goToEnd}>End</button>
        {showEvaluation && (
          <div className="stockfish-evaluation">
            <div>Stockfish Eval: {evaluation}</div>
            <div>{bestLine}</div>
          </div>
        )}
      </div>
    </div>
    {showEditBoard && (
      <div className="edit-board-container" id="EditingOn" style={{ display: "block" }}>
        <EditBoard in_fen={fen} updateFenAndClose={this.updateFenAndCloseEdit} />
      </div>
    )}
  </div>
);
}
}
export default AnalysisBoard;

