import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './LandingPage';
import StockfishGamePage from './StockfishGamePage';
import AnalysisBoardPage from './AnalysisBoardPage';
import SignupPage from './SignupPage';
import BlunderPunisher from './BlunderPunisher';
import WeaknessFinder from './WeaknessFinder';
import FeedbackPage from './FeedbackPage';
import TreeExplorer from './TreeExplorer';

// 1. ERROR HANDLER SETUP - MUST BE FIRST EXECUTED CODE
(function() {
  // Atomic banner creation with guaranteed paint
  const createBanner = (message) => {
    const banner = document.createElement('div');
    // Synchronous style application
    Object.assign(banner.style, {
      position: 'fixed',
      top: '0',
      left: '0',
      right: '0',
      backgroundColor: 'red',
      color: 'white',
      padding: '10px',
      textAlign: 'center',
      zIndex: '2147483647', // Maximum possible z-index
      fontSize: '16px',
      boxShadow: '0 2px 5px rgba(0,0,0,0.2)'
    });

    const paragraph = document.createElement('p');
    paragraph.style.margin = '0';
    paragraph.textContent = message;

    banner.appendChild(paragraph);
    document.documentElement.prepend(banner); // Prepend to HTML root

    return banner;
  };

  // Unified error handler with microtask timing
  const handleError = (error) => {
    const message = error?.message?.includes('ArrayBuffer') 
      ? 'Array Buffer Allocation Failed'
      : 'Unexpected error encountered';

    // 1. Immediately paint banner
    const banner = createBanner(message);

    // 2. Force synchronous layout and paint
    banner.offsetHeight; // Trigger reflow

    // 3. Schedule cleanup and reload
    setTimeout(() => {
      banner.remove();
      window.location.reload();
    }, 3000);
  };

  // Install handlers using capturing phase
  window.addEventListener('error', (event) => {
    handleError(event.error);
    event.preventDefault();
    event.stopImmediatePropagation();
  }, true); // Capture phase

  window.addEventListener('unhandledrejection', (event) => {
    handleError(event.reason);
    event.preventDefault();
    event.stopImmediatePropagation();
  }, true); // Capture phase
})();

function App() {
  return (
    <Router>
      <div>
        <Routes>
          <Route exact path="/" element={<LandingPage />} />
          <Route path="/play-stockfish" element={<StockfishGamePage />} />
          <Route path="/blunder-punisher" element={<BlunderPunisher />} />
          <Route path="/weakness-finder" element={<WeaknessFinder />} />
          <Route path="/analysis-board" element={<AnalysisBoardPage />} />
          <Route path="/tree-explorer" element={<TreeExplorer />} />
          <Route path="/feedback" element={<FeedbackPage />} />
          <Route path="/signup" element={<SignupPage />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
