import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Chess } from 'chess.js';
import '.styles.css';

const BlunderPunisher = () => {
    const [importedGames, setImportedGames] = useState([]);
    const [gamesCount, setGamesCount] = useState(0);

    const handleFileSelect = (event) => {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            const fullPgn = e.target.result;
            let rawGames = fullPgn.split(/\[Event/gi).filter(Boolean);
            let games = rawGames.map(game => '[Event' + game.trim());

            setImportedGames(games);
            setGamesCount(games.length);
        };
        reader.readAsText(file);
    };

    return (
        <div  className="blunder-punisher">
            <header>
                <nav>
                    <ul>
                        <li><Link to="/">Home</Link></li>
                        <li><Link to="/blunder-punisher">Blunder Punisher</Link></li>
                        <li><Link to="/">Weakness Finder</Link></li>
                        <li><Link to="/analysis-board">Analysis Board</Link></li>
                        <li><Link to="#about-us">About Us</Link></li>
                    </ul>
                </nav>
            </header>
            <main>
                <h1>Blunder Punisher</h1>
                <input type="file" onChange={handleFileSelect} />
                <p>Number of games in the file: {gamesCount}</p>
                <div className="games-list">
                    <ul>
                        {importedGames.map((game, index) => (
                            <li key={index}>{game.substring(0, 50)}...</li>
                        ))}
                    </ul>
                </div>
            </main>
        </div>
    );
};

export default BlunderPunisher;
