import React, { useState , useEffect} from 'react';
import { Link, useLocation } from 'react-router-dom';
import AnalysisBoard from './AnalysisBoard';
import './styles.css';

// Import both logo images
import logo from './checkmatewizard_transparent.png'; // Adjust the path as needed
import logoWhite from './checkmatewizard_no_black.png'; // Adjust the path as needed


const AnalysisBoardPage = () => {
    const location = useLocation();
    const initialFen = location.state?.fen || 'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1';
    const [orientation, setOrientation] = useState('white');
    const [fen, setFen] = useState(initialFen); // Use the FEN passed from EditBoard or the default starting position
    const [isLogoHovered, setIsLogoHovered] = useState(false);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);

    // Event listener for window resize
    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth < 1268);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);


    return (
        <div className="landing-page">
            <header>
                <nav>
                    <Link to="/" onMouseEnter={() => setIsLogoHovered(true)} onMouseLeave={() => setIsLogoHovered(false)}>
                        <img src={isLogoHovered ? logoWhite : logo} alt="Home" style={{ height: '39px' }} />
                    </Link>
                    {isMobile ? (
                        <div className="nav-dropdown">
                            <button onClick={() => setIsDropdownOpen(!isDropdownOpen)}>
                                â˜° Menu
                            </button>
                            {isDropdownOpen && (
                                <div className="dropdown-content">
                                    <Link to="/blunder-punisher" onClick={() => setIsDropdownOpen(false)}>Blunder Punisher</Link>
                                    <Link to="/weakness-finder" onClick={() => setIsDropdownOpen(false)}>Weakness Finder</Link>
                                    <Link to="/analysis-board" onClick={() => setIsDropdownOpen(false)}>Analysis Board</Link>
                                    <Link to="/about-player" onClick={() => setIsDropdownOpen(false)}>About Player</Link>
                                </div>
                            )}
                        </div>
                    ) : (
                        <ul>
                            <li><Link to="/blunder-punisher" className="prepare-link">Blunder Punisher</Link></li>
                            <li><Link to="/weakness-finder">Weakness Finder</Link></li>
                            <li><Link to="/analysis-board">Analysis Board</Link></li>
                            <li><Link to="/about-player">About Player</Link></li>
                        </ul>
                    )}
                </nav>
            </header>

            <main>       
                <div className='analysis-board-section'>
                    <AnalysisBoard fen={fen} />
                </div>
            </main>
            <footer>
                <p>&copy; 2024 CheckmateWizard.com All rights reserved.</p>
            </footer>
        </div>
    );
};

export default AnalysisBoardPage;
