import React, { useState } from 'react';
import axios from 'axios';
import { Chess } from 'chess.js'; // Ensure chess.js is available client-side

const ImportGames = () => {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');
  const [gamesList, setGamesList] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [currentMove, setCurrentMove] = useState(0);
  const handleSelectGame = (e) => {
    setSelectedGame(e);
  };




  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a file to upload.');
      return;
    }

    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      const chess = new Chess();
      console.log(1);
      chess.loadPgn(text);
      console.log(2);

      if (!chess.pgn()) {
        console.log(text);
        setUploadStatus('Invalid PGN file. Please upload a valid PGN file.');
        return;
      }

      // Optionally extract moves or any other data from the PGN here
      const moves = chess.history({ verbose: true });
      console.log(moves);
      try {
        // Inside handleUpload, after successful upload
// Assuming the server returns an array of games
        // Send the extracted data to your server instead of the file itself
        const response = await axios.post('http://localhost:3001/api/games/process', { moves }, {
  headers: {
    'Content-Type': 'application/json',
  },
});
setGamesList(response.data); 
        setUploadStatus('File processed successfully!');
      }  catch (error) {
        if (error.response) {
          console.log(error.response.data); // Detailed error from server
          setUploadStatus(error.response.data || 'An error occurred during file processing.');
        } else {
          setUploadStatus('An error occurred during file upload.');
        }
      }


    };
    reader.readAsText(file); // Read the file as text
  };

  return (
    <div>
    <form onSubmit={handleUpload}>
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Upload</button>
      {uploadStatus && <p>{uploadStatus}</p>}
    </form>
  </div>

  );
};

export default ImportGames;
