import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import AnalysisBoard from './AnalysisBoard';
import './styles.css';

// Import both logo images
import logo from './checkmatewizard_transparent.png'; // Adjust the path as needed
import logoWhite from './checkmatewizard_no_black.png'; // Adjust the path as needed


const AnalysisBoardPage = () => {
    const location = useLocation();
    const initialFen = location.state?.fen || 'rnbqkbnr/pppppppp/8/8/8/8/PPPPPPPP/RNBQKBNR w KQkq - 0 1';
    const [orientation, setOrientation] = useState('white');
    const [fen, setFen] = useState(initialFen); // Use the FEN passed from EditBoard or the default starting position
    const [isLogoHovered, setIsLogoHovered] = useState(false);

    useEffect(() => {
        const handleResize = () => setIsMobile(window.innerWidth < 768);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    return (
        <div className="landing-page">
            <header>
            <nav>
                    <ul className={`nav-list ${isMobile ? 'mobile' : ''}`}>
                        <li onMouseEnter={() => setIsLogoHovered(true)} onMouseLeave={() => setIsLogoHovered(false)}>
                            <Link to="/">
                                <img src={isLogoHovered ? logoWhite : logo} alt="Home" className="logo" />
                            </Link>
                        </li>
                        <li><Link to="/blunder-punisher" className="prepare-link">Blunder Punisher</Link></li>
                        <li><Link to="/weakness-finder">Weakness Finder</Link></li>
                        <li><Link to="/analysis-board">Analysis Board</Link></li>
                        <li><Link to="/about-player">About Player</Link></li>
                    </ul>
                </nav>
            </header>

            <main>       
                <div className='analysis-board-section'>
                    <AnalysisBoard fen={fen} />
                </div>
            </main>
            <footer>
            <p>&copy; 2024 CheckmateWizard.com All rights reserved.</p>
            </footer>
        </div>
    );
};

export default AnalysisBoardPage;
