import React, { Component } from 'react';
import './ErrorBoundary.css';

class ErrorBoundary extends Component {
  state = { hasError: false, errorMessage: '' };

  static getDerivedStateFromError(error) {
    // Always return an error state when an error is caught
    return { hasError: true, errorMessage: error.message || 'Unexpected error encountered' };
  }

  componentDidCatch(error, errorInfo) {
    console.error('ErrorBoundary caught an error:', error, errorInfo);
  }

  componentDidUpdate(_, prevState) {
    if (this.state.hasError && prevState.hasError !== this.state.hasError) {
      // Remove the banner after 3 seconds
      setTimeout(() => {
        this.setState({ hasError: false, errorMessage: '' });
      }, 3000); // 3 seconds delay before removing the banner
    }
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-banner">
          <p>{this.state.errorMessage}</p>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
