//UsrName: zapatahall1 pasword: tAma...#1055
import React, { useState } from 'react';
import './styles.css';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Import Axios for making HTTP requests


// Import both logo images
import logo from './squarechesslogolong.png'; // Adjust the path as needed
import logoWhite from './squarechesslogolongwhite.png'; // Adjust the path as needed

const SignupPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [isLogoHovered, setIsLogoHovered] = useState(false);

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };


    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleEmailChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSignup = async () => {
        try {
            const response = await axios.post('http://localhost:3001/api/auth/signup', { username, password });
            console.log(response.data); // Handle successful signup response
        } catch (error) {
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                console.error(error.response.data);
            } else if (error.request) {
                // The request was made but no response was received
                console.error(error.request);
            } else {
                // Something happened in setting up the request that triggered an Error
                console.error('Error', error.message);
            }
        }
    };


    return (
        <div className="signup-page">
            <header>
                <nav>
                    <ul>
                        <li onMouseEnter={() => setIsLogoHovered(true)}
                            onMouseLeave={() => setIsLogoHovered(false)}>
                            <Link to="/">
                                <img src={isLogoHovered ? logoWhite : logo} alt="Home" style={{height: '40px' }} />
                            </Link>
                        </li>
                        <li><Link to="/" className="prepare-link">Prepare</Link></li>
                        <li><Link to="/analysis-board">Analysis Board</Link></li>
                        <li><Link to="#import-games">Import Games</Link></li>
                        <li><Link to="#about-us">About Us</Link></li>
                    </ul>
                </nav>
            </header>

            <main>
                <section className="hero2">
                    <section className="signup-section">
                        <div className="signup-form">
                            <input
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={handleUsernameChange}
                            />
                            <input
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={handlePasswordChange}
                            />
                            <input
                                type="email"
                                placeholder="Email"
                                value={email}
                                onChange={handleEmailChange}
                            />
                            <button className="login-button" onClick={handleSignup}>Sign Up</button>
                        </div>
                    </section>
                    <div className="recommendation-section">
                        <div className="green-box">
                            <p>More than 500 Users Recommended by GMs</p>
                        </div>
                    </div>
                </section>
            </main>

            <footer>
                <p>&copy; 2024 SquareChess. All rights reserved.</p>
            </footer>
        </div>
    );
};

export default SignupPage;
