    function updateFen(fen, sourceSquare, targetSquare, piece) {
    // Split FEN into ranks
    const ranks = fen.split(" ")[0].split("/");
    const pieceChar = piece === 'P' ? 'P' : piece.toLowerCase();

    const sourceFile = sourceSquare.charCodeAt(0) - 'a'.charCodeAt(0);
    const sourceRank = 8 - parseInt(sourceSquare[1]);

    const targetFile = targetSquare.charCodeAt(0) - 'a'.charCodeAt(0);
    const targetRank = 8 - parseInt(targetSquare[1]);

    // Remove the piece from the source rank
    ranks[sourceRank] = updateRank(ranks[sourceRank], sourceFile, null);

    // Add the piece to the target rank
    ranks[targetRank] = updateRank(ranks[targetRank], targetFile, pieceChar);

    // Join the ranks back into a FEN string
    const updatedFen = ranks.join("/") + fen.slice(fen.indexOf(" "));
    return updatedFen;
}

function updateRank(rank, fileIndex, piece) {
    const rankPieces = expandRank(rank);
    rankPieces[fileIndex] = piece || '1'; // Remove or place the piece

    // Compress the rank back into FEN format
    return compressRank(rankPieces.join(''));
}

function expandRank(rank) {
    return rank.replace(/\d/g, (match) => '1'.repeat(parseInt(match))).split('');
}

function compressRank(rank) {
    return rank.replace(/1{1,8}/g, (match) => match.length.toString());
}

