import React, { useState } from 'react';
import axios from 'axios';

const ImportGames = () => {
  const [file, setFile] = useState(null);
  const [uploadStatus, setUploadStatus] = useState('');

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFile(file);

    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const text = e.target.result;
        console.log(text); // Print the file content to the console
      };
      reader.readAsText(file); // Read the file as text
    } else {
      console.log("No file selected");
    }
  };

  const handleUpload = async (e) => {
    const chess = new Chess();
const isLoaded = chess.load_pgn('[Event "Casual Bullet game"] ... { White resigns. } 0-1');

console.log('Is PGN Loaded:', isLoaded);
console.log('PGN:', chess.pgn());
    console.log(1);
    e.preventDefault();
    if (!file) {
      setUploadStatus('Please select a file to upload.');
      return;
    }
    console.log(2);

    const formData = new FormData();
    formData.append('pgnFile', file);
    console.log(3);

    try {

      const response = await axios.post('http://localhost:3001/api/games/upload', formData);

      setUploadStatus('File uploaded successfully!');
    } catch (error) {
        console.log(4);
      if (error.response && error.response.data) {
        setUploadStatus(error.response.data);
      } else {
        setUploadStatus('An error occurred during file upload.');
      }
    }
  };

  return (
    <form onSubmit={handleUpload}>
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Upload</button>
      {uploadStatus && <p>{uploadStatus}</p>}
    </form>
  );
};

export default ImportGames;
