import React, { Component } from "react";
import axios from 'axios';
import PropTypes from "prop-types";
import { Chess } from "chess.js";
import Chessboard from "chessboardjsx";
import EditBoard from "./EditBoard";

const STOCKFISH = window.STOCKFISH;
const game = new Chess();

class AnalysisBoard extends Component {
  static propTypes = {
    children: PropTypes.func,
    fen: PropTypes.string,
    pgn: PropTypes.string, // Add this line
  };

  state = {
    orientation: "white",
    evaluation: "",
    bestLine: "",
    currentPly: 1,
    future: [],
    showEvaluation: false, // Add this line
    moveHistory: [],
    fullGame: [],
    importedGames: [], // Holds imported games
    showDropdown: false, // Toggles dropdown visibility
    selectedGame: null, // To store the selected game for display
    gamesDetails: [] // To store detailed metadata of games
  };
    componentDidMount() {
      if (this.props.pgn) {
          game.loadPgn(this.props.pgn);
          this.future = [];
          this.moveHistory= game.history();
          this.fullGame = this.moveHistory.concat(this.future);

      }else{
        game.reset();
        this.future=[];
        this.moveHistory=[];
        this.fullGame = [];
        this.setState({ fen: "start"});}
      this.engineGame().prepareMove();
  }
  // Method to handle file selection and game import
  handleGameSelect = (pgn) => {
    game.reset();
    const tmp = new Chess();
    tmp.loadPgn(pgn);
    this.setState({ fen: "start"});
    this.future = tmp.history();
    this.moveHistory= tmp.history();
    this.fullGame = this.moveHistory;
    this.engineGame().prepareMove();
  }   
  handleFileSelect = async (event) => {
    console.log(1);
    const file = event.target.files[0];
    if (!file) {
      console.log(2);
      return;
    }
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      const chess = new Chess();
      chess.loadPgn(text);
      console.log(3);
      if (!chess.pgn()) {
        console.log('Invalid PGN file. Please upload a valid PGN file.');
        return;
      }
      try {
        console.log(this.importedGames);
        console.log(4);
        const response = await axios.post('http://localhost:3001/api/games/process', { pgn: text }, {
          headers: {
            'Content-Type': 'application/json',
          },
        });
        console.log(5);
        this.importedGames=response.data;
        console.log(this.importedGames);
      }  catch (error) {
        if (error.response) {
          console.log(error.response.data); // Detailed error from server
          console.log(error.response.data || 'An error occurred during file processing.');
        } else {
          console.log('An error occurred during file upload.');
        }
      }
    };
    console.log(6);
    reader.readAsText(file);
    console.log(7);
