//UsrName: zapatahall1 pasword: tAma...#1055
import React, { useState } from 'react';
import './styles.css';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Import Axios for making HTTP requests

const LandingPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleLogin = async () => {
        try {
            const response = await axios.post('http://localhost:3001/api/auth/login', { username, password });
            console.log(response.data); // Handle successful login response
        } catch (error) {
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                console.error(error.response.data);
            } else if (error.request) {
                // The request was made but no response was received
                // `error.request` is an instance of XMLHttpRequest in the browser and an instance of
                // http.ClientRequest in node.js
                console.error(error.request);
            } else {
                // Something happened in setting up the request that triggered an Error
                console.error('Error', error.message);
            }
        }
    };


    const handleSignup = async () => {
        try {
            const response = await axios.post('http://localhost:3001/api/auth/signup', { username, password });
            console.log(response.data); // Handle successful signup response
        } catch (error) {
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                console.error(error.response.data);
            } else if (error.request) {
                // The request was made but no response was received
                console.error(error.request);
            } else {
                // Something happened in setting up the request that triggered an Error
                console.error('Error', error.message);
            }
        }
    };


    return (
        <div className="landing-page">
            <header>
            <nav>

                </nav>
            </header>

            <main>
                <section className="hero">
                    <section className="login-section">
                        <div className="login-form">
                            <input
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={handleUsernameChange}
                            />
                            <input
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={handlePasswordChange}
                            />
                            <button className="login-button" onClick={handleLogin}>Log In</button>
                            <div className="create-account-div">
                                <p>Don't have an account?</p>
                                <button className="create-account-button" onClick={handleSignup}>Create a SquareChess Account</button>
                            </div>
                        </div>
                    </section>
                    <div className="recommendation-section">
                        <div className="green-box">
                            <p>More than 500 Users Recommended by GMs</p>
                        </div>
                    </div>
                </section>
            </main>

            <footer>
                <p>&copy; 2024 SquareChess. All rights reserved.</p>
            </footer>
        </div>
    );
};

export default LandingPage;
