// ErrorBoundary.js
import React, { Component } from 'react';
import './ErrorBoundary.css'; // Create a CSS file for styling the banner

class ErrorBoundary extends Component {
  state = { hasError: false, errorMessage: '' };

  static getDerivedStateFromError(error) {
    // Return an updated state to indicate that an error has occurred
    return { hasError: true, errorMessage: 'Sorry, an unexpected error occurred.' };
  }

  componentDidCatch(error, errorInfo) {
    console.error("ErrorBoundary caught an error", error, errorInfo);
    // Attempt to reload the page
    localStorage.setItem('hasError', 'true');
    window.location.reload();
  }

  handleClose = () => {
    this.setState({ hasError: false, errorMessage: '' });
  };

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-banner">
          <p>{this.state.errorMessage}</p>
          <button onClick={this.handleClose} className="close-button">X</button>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
